import React, { useState } from 'react';
import { Box, Text, HStack, VStack, Progress, Input, Flex, useBreakpointValue, Button } from '@chakra-ui/react';
import { ChevronDownIcon, ChevronUpIcon } from '@chakra-ui/icons';
import { FixedSizeList as List } from 'react-window';

const ExamCard = ({ title, progress, totalQuestions }) => {
  const cardWidth = useBreakpointValue({ base: "100%", sm: "300px", md: "350px", lg: "400px" });
  const fontSize = useBreakpointValue({ base: "16px", md: "18px", lg: "20px" });
  const imageHeight = useBreakpointValue({ base: "100px", md: "110px", lg: "120px" });
  const buttonFontSize = useBreakpointValue({ base: "14px", md: "15px", lg: "16px" });

  return (
    <Box
      backgroundColor="white"
      borderRadius="12px"
      border="1px solid black"
      boxShadow="0 4px 0 0 black"
      padding={4}
      width={cardWidth}
      flexShrink={0}
    >
      <Text fontSize={fontSize} fontWeight="bold" marginBottom={3}>{title}</Text>
      <Box height={imageHeight} backgroundColor="gray.200" marginBottom={3} />
      <Progress value={progress} max={totalQuestions} colorScheme="blue" marginBottom={2} height="12px" />
      <Flex justifyContent="space-between" alignItems="center" marginTop={2} flexDirection={{ base: "column", sm: "row" }} gap={2}>
        <Text fontSize={buttonFontSize} color="gray.600">{progress} / {totalQuestions} questions</Text>
        <Box
          as="button"
          paddingX={6}
          paddingY={2}
          borderRadius="full"
          backgroundColor="#00bfff"
          color="black"
          fontWeight="bold"
          fontSize={buttonFontSize}
          border="1px solid black"
          boxShadow="0 2px 0 0 black"
          _hover={{ backgroundColor: "#00a6d6" }}
          _active={{ boxShadow: "none", transform: "translateY(2px)" }}
        >
          Continue
        </Box>
      </Flex>
    </Box>
  );
};

const ProviderCard = ({ providerName, exams }) => {
  const fontSize = useBreakpointValue({ base: "24px", md: "26px", lg: "28px" });

  return (
    <Box
      backgroundColor="#f2f2f3"
      borderRadius="20px"
      border="1px solid black"
      boxShadow="0 4px 0 0 black"
      padding={6}
      marginBottom={8}
      width="100%"
    >
      <Text fontSize={fontSize} fontWeight="bold" marginBottom={6}>{providerName}</Text>
      <Box overflowX="auto" paddingBottom={4}>
        <HStack spacing={6}>
          {exams.map((exam, index) => (
            <ExamCard key={index} title={exam.title} progress={exam.progress} totalQuestions={exam.totalQuestions} />
          ))}
        </HStack>
      </Box>
    </Box>
  );
};

const ProviderDropdown = ({ providers, selectedProvider, onSelect }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => setIsOpen(!isOpen);

  const handleSelect = (provider) => {
    onSelect(provider);
    setIsOpen(false);
  };

  return (
    <Box position="relative" width="300px">
      <Button
        onClick={toggleDropdown}
        width="100%"
        bg="white"
        color="black"
        fontWeight={700}
        fontSize="16px"
        lineHeight="19px"
        borderRadius="10px"
        border="1px solid black"
        boxShadow="none"
        _hover={{
          bg: "#00bfff",
          boxShadow: "0 3px 0 0 black",
        }}
        _active={{
          bg: "#00bfff",
          boxShadow: "none",
        }}
        transition="all 0.2s"
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        px={4}
        py={3}
      >
        <Text>{selectedProvider}</Text>
        {isOpen ? <ChevronUpIcon /> : <ChevronDownIcon />}
      </Button>
      {isOpen && (
        <Box
          position="absolute"
          top="100%"
          left={0}
          width="100%"
          height="300px"
          bg="white"
          borderRadius="10px"
          border="1px solid black"
          mt={2}
          zIndex={1}
          overflow="hidden"
        >
          <List
            height={300}
            itemCount={providers.length}
            itemSize={40}
            width="100%"
          >
            {({ index, style }) => (
              <Box
                style={style}
                py={2}
                px={4}
                cursor="pointer"
                _hover={{ bg: "#b3ebf2" }}
                onClick={() => handleSelect(providers[index])}
                borderBottom={index < providers.length - 1 ? "1px solid #E2E8F0" : "none"}
              >
                <Text fontWeight={700} fontSize="16px" lineHeight="19px" color="black">
                  {providers[index]}
                </Text>
              </Box>
            )}
          </List>
        </Box>
      )}
    </Box>
  );
};

const ProviderExamsCard = () => {
  const allProviders = [
    "Microsoft", "Cisco", "CompTIA", "Amazon", "Oracle", "Isaca", "VMware", "Salesforce", "Google", "PMI",
    "ECCouncil", "ServiceNow", "Fortinet", "Dell", "Juniper", "Checkpoint", "Palo Alto Networks", "ISC", "HP",
    "Snowflake", "Citrix", "IBM", "The Open Group", "Splunk", "Netapp", "IIA", "IIBA", "LPI", "PRINCE2", "CyberArk",
    "APICS", "IAPP", "Huawei", "ACAMS", "Avaya", "HashiCorp", "Pegasystems", "Databricks", "Nutanix", "ITIL",
    "UiPath", "Test Prep", "GIAC", "Scrum", "Adobe", "F5", "Mulesoft", "Veeam", "SAP", "CrowdStrike",
    "Alcatel-Lucent", "CSA", "Nokia", "Six Sigma", "NetSuite", "Blue Prism", "Python Institute", "Linux Foundation",
    "Genesys", "AHIMA", "API", "RedHat", "SAS Institute", "Tableau", "ISTQB", "CWNP", "ASQ", "Atlassian", "AndroidATC",
    "ASIS", "BICSI", "Exin", "CIW", "iSQI", "Appian", "GAQM", "WatchGuard", "Veritas", "Vmedu", "Zend", "Arista",
    "Blockchain", "HRCI", "GARP", "QlikView", "Alibaba", "ISA", "English Test Preparation", "NFPA", "Axis Communications",
    "IAAP", "Scaled Agile", "BACB", "Sitecore", "Teradata", "NADCA", "SOA", "Versa Networks", "ABA", "CertNexus",
    "Hitachi", "iSAQB", "ACSM", "DSCI", "SolarWinds", "Symantec", "AAPC", "FINRA", "BCS", "CNCF", "ACFE", "Mirantis",
    "SANS", "Infor", "Magento", "Novell", "nCino", "NI", "A10 Networks", "Apple", "Riverbed", "AAFM India", "McAfee",
    "DMI", "Meta", "SNIA", "WorldatWork", "AHIP", "AHLEI", "Guidance Software", "HCL Software Academy", "NCMA",
    "Netskope", "CA Technologies", "C++ Institute", "Tibco", "Aruba", "Cloudera", "FileMaker", "PEOPLECERT", "USGBC",
    "AICPA", "AIWMI", "APSE", "Autodesk", "Blue Coat", "Certinia", "GMAC", "HAAD", "Logical Operations", "NACVA", "Unlimited"
  ];

  const [selectedProvider, setSelectedProvider] = useState(allProviders[0]);
  const [providers, setProviders] = useState([
    {
      name: "Amazon Web Services (AWS)",
      exams: [
        { title: "AWS Certified Cloud Practitioner", progress: 65, totalQuestions: 100 },
        { title: "AWS Certified Solutions Architect - Associate", progress: 30, totalQuestions: 100 },
        { title: "AWS Certified Developer - Associate", progress: 45, totalQuestions: 100 },
        { title: "AWS Certified SysOps Administrator - Associate", progress: 20, totalQuestions: 100 },
        { title: "AWS Certified DevOps Engineer - Professional", progress: 10, totalQuestions: 100 },
      ]
    },
    // Add more providers here as needed
  ]);

  return (
    <VStack spacing={8} align="stretch" width="100%">
      <Flex justifyContent="space-between" alignItems="center">
        <Input 
          placeholder="Search exams..." 
          size="lg" 
          width={{ base: "100%", md: "60%" }} 
        />
        <ProviderDropdown 
          providers={allProviders}
          selectedProvider={selectedProvider}
          onSelect={setSelectedProvider}
        />
      </Flex>
      {providers.map((provider, index) => (
        <ProviderCard 
          key={index} 
          providerName={provider.name} 
          exams={provider.exams} 
        />
      ))}
    </VStack>
  );
};

export default ProviderExamsCard;
