import React from 'react';
import { Box, Text, Flex, SimpleGrid, useBreakpointValue } from '@chakra-ui/react';
import ExamCard from './ExamCard';

const ProviderCard = ({ providerName, exams }) => {
  const fontSize = useBreakpointValue({ base: "24px", md: "26px", lg: "28px" });

  return (
    <Box
      backgroundColor="#f2f2f3"
      borderRadius="20px"
      border="1px solid black"
      boxShadow="0 4px 0 0 black"
      padding={6}
      marginBottom={8}
      width="100%"
    >
      <Text fontSize={fontSize} fontWeight="bold" marginBottom={6}>{providerName}</Text>
      <SimpleGrid columns={{ base: 1, sm: 2, md: 3, lg: 4, xl: 5 }} spacing={6}>
        {exams.map((exam, index) => (
          <ExamCard 
            key={index} 
            title={exam.title} 
            progress={exam.progress} 
            totalQuestions={exam.totalQuestions} 
          />
        ))}
      </SimpleGrid>
    </Box>
  );
};

export default ProviderCard;
