import React from 'react';
import { Box, Text, VStack, Button, keyframes } from '@chakra-ui/react';
import { FaFileDownload } from "react-icons/fa";
import { ChevronRight } from "lucide-react";

const gradientAnimation = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const DownloadBox = () => {
  return (
    <Box position="relative" width="100%" maxWidth="2xl" mx="auto">
      <Button
        width="100%"
        height="auto"
        py={12}
        fontSize="2xl"
        fontWeight="semibold"
        color="gray.500"
        bg="gray.100"
        _hover={{ bg: "gray.100" }}
        cursor="not-allowed"
        disabled
        borderRadius="20px"
        border="1px solid black"
        boxShadow="0 4px 0 0 black"
      >
        <VStack spacing={2}>
          <FaFileDownload size="32px" />
          <Text>Download exam questions</Text>
          <Text fontSize="lg" fontWeight="normal">PDF</Text>
        </VStack>
      </Button>

      <Button
        position="absolute"
        top="-20px"
        right="-20px"
        transform="rotate(12deg)"
        bg="linear-gradient(45deg, #ffaa40, #9c40ff, #ffaa40)"
        backgroundSize="200% 200%"
        animation={`${gradientAnimation} 3s ease infinite`}
        color="white"
        fontWeight="bold"
        fontSize="lg"
        borderRadius="full"
        px={6}
        py={3}
        border="1px solid black"
        boxShadow="0 2px 0 0 black"
        _hover={{
          bg: "linear-gradient(45deg, #ff9020, #7c20ff, #ff9020)",
        }}
        _active={{
          boxShadow: "none",
          transform: "rotate(12deg) translateY(2px)",
        }}
      >
        ðŸŽ‰ Go Premium! <ChevronRight size={16} style={{ marginLeft: '4px' }} />
      </Button>
    </Box>
  );
};

export default DownloadBox;
