import React, { useState } from 'react';
import { ChakraProvider, extendTheme, Box, Text, Flex, Icon } from '@chakra-ui/react';
import '@fontsource-variable/karla/wght.css';
import { AiOutlineStar } from 'react-icons/ai';
import { PiSealFill } from 'react-icons/pi';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
});

const OutlinedStar = ({ filled, color }) => (
  <Icon viewBox="0 0 1024 1024" width="1em" height="1em">
    <path
      fill={filled ? color : 'none'}
      stroke={color}
      strokeWidth="60"
      d="M908.1 353.1l-253.9-36.9L540.7 86.1c-3.1-6.3-8.2-11.4-14.5-14.5-15.8-7.8-35-1.3-42.9 14.5L369.8 316.2l-253.9 36.9c-7 1-13.4 4.3-18.3 9.3a32.05 32.05 0 0 0 .6 45.3l183.7 179.1-43.4 252.9a31.95 31.95 0 0 0 46.4 33.7L512 754l227.1 119.4c6.2 3.3 13.4 4.4 20.3 3.2 17.4-3 29.1-19.5 26.1-36.9l-43.4-252.9 183.7-179.1c5-4.9 8.3-11.3 9.3-18.3 2.7-17.5-9.5-33.7-27-36.3z"
    />
  </Icon>
);

const IconBox = ({ bgColor, size, iconScale, onClick, isStarFilled }) => {
  const iconSize = `${parseInt(size) * iconScale}px`;

  return (
    <Box 
      position="relative" 
      width={size} 
      height={size} 
      onClick={onClick}
      cursor="pointer"
      transition="all 0.3s ease"
      _hover={{
        '& > svg': {
          color: '#00bfff',
        }
      }}
    >
      <PiSealFill size={size} color={bgColor} />
      <Flex
        position="absolute"
        top="50%"
        left="50%"
        transform="translate(-50%, -50%)"
        alignItems="center"
        justifyContent="center"
      >
        <OutlinedStar filled={isStarFilled} color={isStarFilled ? "#FFD700" : "black"} fontSize={iconSize} />
      </Flex>
    </Box>
  );
};

const MainPage = () => {
  const [isStarFilled, setIsStarFilled] = useState(false);
  const questionNumber = 1;
  const totalQuestions = 10;
  const questionText = "A company is planning to run a global marketing application in the AWS Cloud. The application will feature videos that can be viewed by users. The company must ensure that all users can view these videos with low latency. Which AWS service should the company use to meet this requirement?";

  const toggleStar = () => {
    setIsStarFilled(!isStarFilled);
  };

  return (
    <ChakraProvider theme={theme}>
      <Flex justifyContent="center" alignItems="center" h="100vh" bg="white">
        <Box
          w="95%"
          maxW="800px"
          bg="#b3ebf2"
          borderRadius="20px"
          border="1px solid black"
          boxShadow="0 4px 0 0 black"
          position="relative"
          overflow="hidden"
          p="6"
        >
          <Flex justifyContent="space-between" alignItems="center" mb="4">
            <Box
              bg="#00bfff"
              px="3"
              py="1"
              borderRadius="10px"
              mt="4"
            >
              <Text fontSize="18px" fontWeight="700" color="black">
                Question {questionNumber} of {totalQuestions}
              </Text>
            </Box>
            <IconBox
              bgColor="white"
              size="32px"
              iconScale={0.6}
              onClick={toggleStar}
              isStarFilled={isStarFilled}
            />
          </Flex>
          <Box mt="4">
            <Text fontSize="20px" fontWeight="500" color="black">
              {questionText}
            </Text>
          </Box>
        </Box>
      </Flex>
    </ChakraProvider>
  );
};

export default MainPage;
