import React from 'react';
import { Box, ChakraProvider, extendTheme, Flex } from '@chakra-ui/react';
import { PiSealQuestion } from 'react-icons/pi';
import { MdEmail } from 'react-icons/md';
import { IoPersonAddOutline } from 'react-icons/io5';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
});

const IconSeal = ({ icon: Icon, bgColor = '#00bfff', size = '40px', ...props }) => {
  return (
    <Box
      display="inline-flex"
      alignItems="center"
      justifyContent="center"
      bg={bgColor}
      borderRadius="12px"
      p="2"
      {...props}
    >
      {Icon ? (
        <Icon size={size} color="black" />
      ) : (
        <PiSealQuestion size={size} color="black" />
      )}
    </Box>
  );
};

// Example usage component
const IconSealExamples = () => {
  return (
    <ChakraProvider theme={theme}>
      <Flex gap={4} p={4} bg="gray.100" justifyContent="center" alignItems="center" minHeight="100vh">
        <IconSeal icon={MdEmail} bgColor="#00bfff" size="24px" />
        <IconSeal icon={IoPersonAddOutline} bgColor="#b3ebf2" size="24px" />
        <IconSeal /> {/* This will use the default seal icon */}
      </Flex>
    </ChakraProvider>
  );
};

// Export both the component and the example usage
export { IconSeal, IconSealExamples };

// Default export for easy import and rendering of examples
export default IconSealExamples;
