import React from 'react';
import { ChakraProvider, extendTheme, Box, Button, VStack, HStack } from '@chakra-ui/react';
import '@fontsource-variable/karla/wght.css';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
  components: {
    Button: {
      baseStyle: {
        fontWeight: '700',
        textTransform: 'uppercase',
        borderRadius: 'full',
        boxShadow: '0 4px 0 0 black',
      },
      variants: {
        referral: {
          height: '48px',
          fontSize: '16px',
          px: '24px',
          bg: '#abf701',
          color: 'black',
          border: '2px solid black',
          _hover: {
            bg: '#9ee600',
          },
          _active: {
            bg: '#8cd500',
            boxShadow: '0 0 0 0 black',
            transform: 'translateY(4px)',
          },
        },
      },
    },
  },
});

const ReferralButton = ({ children, width, ...props }) => (
  <Button variant="referral" width={width} {...props}>
    {children}
  </Button>
);

const MainPage = () => {
  return (
    <ChakraProvider theme={theme}>
      <Box bg="white" minHeight="100vh" display="flex" justifyContent="center" alignItems="center">
        <VStack spacing={4} align="stretch" width="100%" maxWidth="600px" px={4}>
          <HStack spacing={4} justify="center">
            <ReferralButton width="50%">COPY REFERRAL LINK</ReferralButton>
            <ReferralButton width="50%">SHARE REFERRAL</ReferralButton>
          </HStack>
          <HStack spacing={4} justify="center">
            <ReferralButton width="calc(50% - 32px)">VIEW STATS</ReferralButton>
            <ReferralButton width="calc(50% - 32px)">SETTINGS</ReferralButton>
          </HStack>
        </VStack>
      </Box>
    </ChakraProvider>
  );
};

export default MainPage;
