import React, { useState } from 'react';
import { Box, Button, ButtonGroup, Flex, Text } from '@chakra-ui/react';
import { PiArrowLeftBold, PiArrowRightBold } from "react-icons/pi";

const TabButton = ({ children, isSelected, ...props }) => (
  <Button
    variant="referral"
    bg={isSelected ? '#00bfff' : '#f2f2f3'}
    color={isSelected ? 'black' : 'gray.600'}
    {...props}
  >
    {children}
  </Button>
);

const NavIconBox = ({ icon: Icon, onClick }) => {
  const [isActive, setIsActive] = useState(false);
  const size = '40px';
  const iconScale = 0.5;
  const iconSize = `${parseInt(size) * iconScale}px`;
  const borderThickness = '0.5px';

  const handleMouseDown = () => setIsActive(true);
  const handleMouseUp = () => setIsActive(false);
  const handleMouseLeave = () => setIsActive(false);

  return (
    <Box
      position="relative"
      width={size}
      height={size}
      onClick={onClick}
      onMouseDown={handleMouseDown}
      onMouseUp={handleMouseUp}
      onMouseLeave={handleMouseLeave}
      cursor="pointer"
      transition="all 0.1s ease"
      transform={isActive ? 'scale(0.95)' : 'scale(1)'}
      userSelect="none"
      style={{
        WebkitTransform: isActive ? 'scale(0.95)' : 'scale(1)',
        MozTransform: isActive ? 'scale(0.95)' : 'scale(1)',
        msTransform: isActive ? 'scale(0.95)' : 'scale(1)',
      }}
    >
      <Box
        position="absolute"
        top="0"
        left="0"
        width="100%"
        height="100%"
        borderRadius="50%"
        border={borderThickness + " solid black"}
        backgroundColor="transparent"
      />
      <Flex
        position="absolute"
        top="50%"
        left="50%"
        transform="translate(-50%, -50%)"
        alignItems="center"
        justifyContent="center"
        width="100%"
        height="100%"
      >
        <Icon 
          size={iconSize} 
          color={isActive ? "#00bfff" : "black"} 
          transition="color 0.2s ease"
        />
      </Flex>
    </Box>
  );
};

const TabList = ({ tabs, onTabChange }) => {
  const [selectedTab, setSelectedTab] = useState(tabs[0]);

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
    onTabChange(tab);
  };

  return (
    <Box bg="white" display="flex" flexDirection="column" alignItems="center" width="100%" mb={4}>
      <ButtonGroup isAttached variant="referral" width="100%" mb={4}>
        {tabs.map((tab) => (
          <TabButton
            key={tab}
            isSelected={selectedTab === tab}
            onClick={() => handleTabChange(tab)}
            flex={1}
          >
            {tab}
          </TabButton>
        ))}
      </ButtonGroup>

      <Flex width="100%" justifyContent="space-between" alignItems="center" py={4}>
        <NavIconBox icon={PiArrowLeftBold} onClick={() => console.log('Navigate left')} />
        <Text fontSize="lg" color="gray.600">
          {selectedTab}
        </Text>
        <NavIconBox icon={PiArrowRightBold} onClick={() => console.log('Navigate right')} />
      </Flex>
    </Box>
  );
};

export default TabList;
