import React from 'react';
import { Box, VStack } from '@chakra-ui/react';
import SearchBar from './SearchBar';
import TabList from './TabList';
import QuestionBox from './QuestionBox';
import OptionsBox from './OptionsBox';
import AnswerBox from './AnswerBox';

const QuestionPanel = ({
  width = "100%",
  onSearch,
  onShuffle,
  onReset,
  onSubmit,
  tabs,
  onTabChange,
  questionNumber,
  totalQuestions,
  questionData,
  isStarFilled,
  toggleStar,
}) => {
  if (!questionData) {
    return (
      <Box width={width}>
        <SearchBar
          onSearch={onSearch}
          onShuffle={onShuffle}
          onReset={onReset}
          onSubmit={onSubmit}
        />
        <TabList tabs={tabs} onTabChange={onTabChange} />
        <Box padding={4}>Loading question data...</Box>
      </Box>
    );
  }

  return (
    <Box width={width}>
      <SearchBar
        onSearch={onSearch}
        onShuffle={onShuffle}
        onReset={onReset}
        onSubmit={onSubmit}
      />
      <TabList tabs={tabs} onTabChange={onTabChange} />
      <VStack spacing={4} align="stretch">
        <QuestionBox
          questionNumber={questionNumber}
          totalQuestionsInTopic={totalQuestions}
          questionData={questionData}
          isStarFilled={isStarFilled}
          toggleStar={toggleStar}
        />
        <OptionsBox options={questionData.options || []} />
        <AnswerBox
          answer={questionData.answer || ''}
          answerDescription={questionData.answerDescription || ''}
          votes={questionData.votes || []}
        />
      </VStack>
    </Box>
  );
};

export default QuestionPanel;
