import React from 'react';
import { Box, ChakraProvider, extendTheme, Flex } from '@chakra-ui/react';
import { MdEmail } from 'react-icons/md';
import { IoPersonAddOutline } from 'react-icons/io5';
import { BsQuestionLg } from 'react-icons/bs';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
});

const IconBox = ({
  icon: Icon,
  bgColor = '#00bfff',
  size = '48px',
  containerType = 'box',
  borderThickness = 1,
  iconScale = 0.6,
  ...props
}) => {
  const iconSize = `${parseInt(size) * iconScale}px`;
  const sizeNum = parseInt(size);

  if (containerType === 'seal') {
    return (
      <Box position="relative" width={size} height={size} {...props}>
        <svg width={size} height={size} viewBox="0 0 100 100">
          {/* Background circle */}
          <circle cx="50" cy="50" r="48" fill={bgColor} />
          {/* Border */}
          <circle
            cx="50"
            cy="50"
            r="48"
            fill="none"
            stroke="black"
            strokeWidth={borderThickness * 2}
          />
          {/* Scalloped edge */}
          <path
            d="M50,2 C50,2 100,25 100,50 C100,75 50,98 50,98 C50,98 0,75 0,50 C0,25 50,2 50,2 Z"
            fill="none"
            stroke="black"
            strokeWidth={borderThickness}
          />
        </svg>
        <Flex
          position="absolute"
          top="50%"
          left="50%"
          transform="translate(-50%, -50%)"
          alignItems="center"
          justifyContent="center"
          width={iconSize}
          height={iconSize}
        >
          {Icon ? (
            <Icon size={iconSize} color="black" />
          ) : (
            <BsQuestionLg size={iconSize} color="black" />
          )}
        </Flex>
      </Box>
    );
  } else {
    // Original box with borderRadius
    return (
      <Flex
        width={size}
        height={size}
        bg={bgColor}
        borderRadius="12px"
        alignItems="center"
        justifyContent="center"
        {...props}
      >
        {Icon ? (
          <Icon size={iconSize} color="black" />
        ) : (
          <BsQuestionLg size={iconSize} color="black" />
        )}
      </Flex>
    );
  }
};

const IconBoxExamples = () => {
  return (
    <ChakraProvider theme={theme}>
      <Flex
        gap={4}
        p={4}
        bg="gray.100"
        justifyContent="center"
        alignItems="center"
        flexWrap="wrap"
      >
        {/* Seals with different border thicknesses */}
        <IconBox
          icon={MdEmail}
          bgColor="#00bfff"
          size="64px"
          containerType="seal"
          borderThickness={1}
          iconScale={0.5}
        />
        <IconBox
          icon={IoPersonAddOutline}
          bgColor="#b3ebf2"
          size="64px"
          containerType="seal"
          borderThickness={2}
          iconScale={0.7}
        />
        <IconBox
          bgColor="#00bfff"
          size="64px"
          containerType="seal"
          borderThickness={3}
          iconScale={0.6}
        />
        {/* Original Box Examples */}
        <IconBox
          icon={MdEmail}
          bgColor="#00bfff"
          size="48px"
          iconScale={0.6}
        />
        <IconBox
          icon={IoPersonAddOutline}
          bgColor="#b3ebf2"
          size="48px"
          iconScale={0.6}
        />
        <IconBox bgColor="#00bfff" size="48px" iconScale={0.6} />
      </Flex>
    </ChakraProvider>
  );
};

export { IconBox, IconBoxExamples };
export default IconBoxExamples;
