import React from 'react';
import { Box, Text, VStack, Button, useTheme } from '@chakra-ui/react';
import { FaFileDownload, FaChevronRight } from "react-icons/fa";

const DownloadBox = () => {
  const theme = useTheme();

  return (
    <Box position="relative" width="100%" maxWidth="2xl" mx="auto">
      <Button
        width="100%"
        height="auto"
        py={12}
        fontSize="2xl"
        fontWeight="semibold"
        color="gray.500"
        bg="gray.100"
        _hover={{ bg: "gray.100" }}
        cursor="not-allowed"
        disabled
        borderRadius="20px"
        border="1px solid black"
        boxShadow="0 4px 0 0 black"
      >
        <VStack spacing={2}>
          <FaFileDownload size="32px" />
          <Text>Download exam questions</Text>
          <Text fontSize="lg" fontWeight="normal">PDF</Text>
        </VStack>
      </Button>

      <Button
        position="absolute"
        top="-20px"
        right="-20px"
        transform="rotate(12deg)"
        bgGradient="linear(to-r, #ffaa40, #9c40ff, #ffaa40)"
        color="white"
        fontWeight="bold"
        fontSize="lg"
        borderRadius="full"
        px={6}
        py={3}
        border="1px solid black"
        boxShadow="0 2px 0 0 black"
        _hover={{
          bgGradient: "linear(to-r, #ff9020, #7c20ff, #ff9020)",
        }}
        _active={{
          boxShadow: "none",
          transform: "rotate(12deg) translateY(2px)",
        }}
      >
        ðŸŽ‰ Go Premium! <Box as={FaChevronRight} display="inline-block" ml={1} />
      </Button>
    </Box>
  );
};

export default DownloadBox;
