import React from 'react';
import { Flex, Button, Box, Text } from '@chakra-ui/react';
import { ChevronLeftIcon, ChevronRightIcon } from '@chakra-ui/icons';

const Pagination = ({ currentPage, totalPages, onPageChange }) => {
  const getPageNumbers = () => {
    const pageNumbers = [];
    if (totalPages <= 7) {
      for (let i = 1; i <= totalPages; i++) {
        pageNumbers.push(i);
      }
    } else {
      if (currentPage <= 4) {
        for (let i = 1; i <= 5; i++) {
          pageNumbers.push(i);
        }
        pageNumbers.push('...');
        pageNumbers.push(totalPages);
      } else if (currentPage >= totalPages - 3) {
        pageNumbers.push(1);
        pageNumbers.push('...');
        for (let i = totalPages - 4; i <= totalPages; i++) {
          pageNumbers.push(i);
        }
      } else {
        pageNumbers.push(1);
        pageNumbers.push('...');
        for (let i = currentPage - 1; i <= currentPage + 1; i++) {
          pageNumbers.push(i);
        }
        pageNumbers.push('...');
        pageNumbers.push(totalPages);
      }
    }
    return pageNumbers;
  };

  const pageNumbers = getPageNumbers();

  return (
    <Flex justifyContent="center" alignItems="center" mt={8} mb={4}>
      <Button
        onClick={() => onPageChange(currentPage - 1)}
        isDisabled={currentPage === 1}
        mr={2}
        bg="transparent"
        color="black"
        _hover={{ color: currentPage === 1 ? "black" : "#00bfff" }}
        _active={{ color: currentPage === 1 ? "black" : "#0095cc" }}
        leftIcon={<ChevronLeftIcon />}
        variant="ghost"
        cursor={currentPage === 1 ? "not-allowed" : "pointer"}
      >
        Previous
      </Button>
      {pageNumbers.map((number, index) => (
        number === '...' ? (
          <Text key={index} mx={1}>...</Text>
        ) : (
          <Box
            key={index}
            as="button"
            mx={1}
            width="40px"
            height="40px"
            display="flex"
            alignItems="center"
            justifyContent="center"
            borderRadius="lg"
            bg={currentPage === number ? "#00bfff" : "white"}
            color="black"
            fontWeight="bold"
            border="1px solid black"
            boxShadow="0 2px 0 0 black"
            onClick={() => onPageChange(number)}
            _hover={{ bg: currentPage === number ? "#00bfff" : "#b3ebf2" }}
            _active={{ boxShadow: "none", transform: "translateY(2px)" }}
          >
            {number}
          </Box>
        )
      ))}
      <Button
        onClick={() => onPageChange(currentPage + 1)}
        isDisabled={currentPage === totalPages}
        ml={2}
        bg="transparent"
        color="black"
        _hover={{ color: currentPage === totalPages ? "black" : "#00bfff" }}
        _active={{ color: currentPage === totalPages ? "black" : "#0095cc" }}
        rightIcon={<ChevronRightIcon />}
        variant="ghost"
        cursor={currentPage === totalPages ? "not-allowed" : "pointer"}
      >
        Next
      </Button>
    </Flex>
  );
};

export default Pagination;
