import React from 'react';
import { Box, ChakraProvider, extendTheme, Flex } from '@chakra-ui/react';
import { MdEmail } from 'react-icons/md';
import { IoPersonAddOutline } from 'react-icons/io5';
import { BsQuestionLg } from 'react-icons/bs';
import { PiSealFill } from 'react-icons/pi'; // Use the filled version of the seal icon

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
});

const IconBox = ({
  icon: Icon,
  bgColor = '#00bfff',
  size = '48px',
  containerType = 'box',
  ...props
}) => {
  const iconSize = `${parseInt(size) * 0.6}px`;

  if (containerType === 'seal') {
    return (
      <Box position="relative" width={size} height={size} {...props}>
        {/* Outer Seal Icon filled with bgColor */}
        <PiSealFill size={size} color={bgColor} />
        {/* Inner Icon */}
        <Flex
          position="absolute"
          top="50%"
          left="50%"
          transform="translate(-50%, -50%)"
          alignItems="center"
          justifyContent="center"
        >
          {Icon ? (
            <Icon size={iconSize} color="black" />
          ) : (
            <BsQuestionLg size={iconSize} color="black" />
          )}
        </Flex>
      </Box>
    );
  } else {
    // Original box with borderRadius
    return (
      <Flex
        width={size}
        height={size}
        bg={bgColor}
        borderRadius="12px"
        alignItems="center"
        justifyContent="center"
        {...props}
      >
        {Icon ? (
          <Icon size={iconSize} color="black" />
        ) : (
          <BsQuestionLg size={iconSize} color="black" />
        )}
      </Flex>
    );
  }
};

const IconBoxExamples = () => {
  return (
    <ChakraProvider theme={theme}>
      <Flex
        gap={4}
        p={4}
        bg="gray.100"
        justifyContent="center"
        alignItems="center"
        minHeight="100vh"
      >
        {/* Seal Container Examples */}
        <IconBox
          icon={MdEmail}
          bgColor="#00bfff"
          size="48px"
          containerType="seal"
        />
        <IconBox
          icon={IoPersonAddOutline}
          bgColor="#b3ebf2"
          size="48px"
          containerType="seal"
        />
        <IconBox bgColor="#00bfff" size="48px" containerType="seal" />
        {/* Original Box Examples */}
        <IconBox icon={MdEmail} bgColor="#00bfff" size="48px" />
        <IconBox icon={IoPersonAddOutline} bgColor="#b3ebf2" size="48px" />
        <IconBox bgColor="#00bfff" size="48px" />
      </Flex>
    </ChakraProvider>
  );
};

export { IconBox, IconBoxExamples };
export default IconBoxExamples;
