import React from 'react';
import { Box, Text, VStack, HStack, Progress, Image } from '@chakra-ui/react';

const ExamItem = ({ title, progress, totalQuestions, badgeUrl }) => (
  <HStack spacing={4} alignItems="center" width="100%">
    <Box width="20px" height="20px" borderLeft="1px solid" borderBottom="1px solid" />
    <Image src={badgeUrl} alt={title} boxSize="30px" />
    <Text flex={1}>{title}</Text>
    <Progress value={progress} max={totalQuestions} width="100px" />
    <Text>{progress} / {totalQuestions}</Text>
  </HStack>
);

const ProviderList = ({ providers }) => {
  return (
    <VStack align="stretch" spacing={6}>
      {providers.map((provider, index) => (
        <Box key={index} borderWidth={1} borderRadius="lg" p={4}>
          <Text fontSize="xl" fontWeight="bold" mb={4}>{provider.name}</Text>
          <VStack align="stretch" spacing={2}>
            {provider.exams.map((exam, examIndex) => (
              <ExamItem
                key={examIndex}
                title={exam.title}
                progress={exam.progress}
                totalQuestions={exam.totalQuestions}
                badgeUrl={exam.badgeUrl} // You'll need to add this to your exam data
              />
            ))}
          </VStack>
        </Box>
      ))}
    </VStack>
  );
};

export default ProviderList;
