import React from 'react';
import { ChakraProvider, extendTheme, Box, Text, Flex } from '@chakra-ui/react';
import '@fontsource-variable/karla/wght.css';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
});

const QuizQuestionBox = ({ questionNumber, totalQuestions, questionText }) => {
  return (
    <ChakraProvider theme={theme}>
      <Flex justifyContent="center" alignItems="center" h="100vh" bg="white">
        <Box
          w="90%"
          maxW="600px"
          bg="#b3ebf2"
          borderRadius="20px"
          border="1px solid black"
          boxShadow="0 4px 0 0 black"
          position="relative"
          overflow="hidden"
          p="6"
        >
          <Box
            position="absolute"
            top="4px"
            left="16px"
            bg="#00bfff"
            px="3"
            py="1"
            borderRadius="10px"
          >
            <Text fontSize="18px" fontWeight="700" color="black">
              Question {questionNumber} of {totalQuestions}
            </Text>
          </Box>
          <Box mt="8">
            <Text fontSize="20px" fontWeight="500" color="black" mt="4">
              {questionText}
            </Text>
          </Box>
        </Box>
      </Flex>
    </ChakraProvider>
  );
};

export default QuizQuestionBox;
