import React from 'react';
import { Box, Text, VStack, HStack, Progress } from '@chakra-ui/react';

const ExamCard = ({ title, progress, totalQuestions }) => (
  <Box
    bg="white"
    borderRadius="12px"
    border="1px solid black"
    boxShadow="0 4px 0 0 black"
    p={4}
    mb={4}
    width="100%"
  >
    <Text fontSize="18px" fontWeight="bold" mb={2}>{title}</Text>
    <Box height="100px" bg="gray.200" mb={2} /> {/* Placeholder for badge image */}
    <Progress value={progress} max={totalQuestions} colorScheme="blue" mb={2} />
    <Text fontSize="14px" color="gray.600">{progress} / {totalQuestions} questions</Text>
    <Box
      as="button"
      mt={2}
      px={4}
      py={2}
      borderRadius="full"
      bg="#00bfff"
      color="black"
      fontWeight="bold"
      border="1px solid black"
      boxShadow="0 2px 0 0 black"
      _hover={{ bg: "#00a6d6" }}
      _active={{ boxShadow: "none", transform: "translateY(2px)" }}
    >
      Continue
    </Box>
  </Box>
);

const ProviderCard = ({ providerName, exams }) => (
  <Box
    bg="#f2f2f3"
    borderRadius="20px"
    border="1px solid black"
    boxShadow="0 4px 0 0 black"
    p={6}
    maxWidth="800px"
    width="100%"
  >
    <Text fontSize="24px" fontWeight="bold" mb={4}>{providerName}</Text>
    <VStack spacing={4} align="stretch">
      {exams.map((exam, index) => (
        <ExamCard key={index} {...exam} />
      ))}
    </VStack>
  </Box>
);

const ProviderExamsCard = () => {
  const awsExams = [
    { title: "AWS Certified Cloud Practitioner", progress: 65, totalQuestions: 100 },
    { title: "AWS Certified Solutions Architect - Associate", progress: 30, totalQuestions: 100 },
    { title: "AWS Certified Developer - Associate", progress: 45, totalQuestions: 100 },
    { title: "AWS Certified SysOps Administrator - Associate", progress: 20, totalQuestions: 100 },
    { title: "AWS Certified DevOps Engineer - Professional", progress: 10, totalQuestions: 100 },
  ];

  return (
    <ProviderCard providerName="Amazon Web Services (AWS)" exams={awsExams} />
  );
};

export default ProviderExamsCard;
