import React from 'react';
import { ChakraProvider, extendTheme, Box, Button, Flex } from '@chakra-ui/react';
import '@fontsource-variable/karla/wght.css';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
  components: {
    Button: {
      variants: {
        referral: {
          height: '48px',
          fontSize: '16px',
          px: '24px',
          bg: '#abf701',
          color: 'black',
          borderRadius: 'full',
          border: '2px solid black',
          fontWeight: 700,
          textTransform: 'uppercase',
          boxShadow: '0 4px 0 0 black',
          _hover: {
            bg: '#abf701',
          },
          _active: {
            bg: '#abf701',
            transform: 'translateY(4px)',
            boxShadow: 'none',
          },
        },
      },
    },
  },
});

const ReferralButton = ({ children, width, ...props }) => (
  <Button variant="referral" width={width} {...props}>
    {children}
  </Button>
);

const MainPage = () => {
  return (
    <ChakraProvider theme={theme}>
      <Box bg="white" minHeight="100vh" display="flex" justifyContent="center" alignItems="center">
        <Flex direction="column" maxWidth="600px" width="100%" px={4} gap={4}>
          <Flex gap={4}>
            <ReferralButton flex={1}>COPY REFERRAL LINK</ReferralButton>
            <ReferralButton flex={1}>SHARE REFERRAL</ReferralButton>
          </Flex>
          <Flex gap={4}>
            <ReferralButton flex={1}>VIEW STATS</ReferralButton>
            <ReferralButton flex={1}>SETTINGS</ReferralButton>
          </Flex>
        </Flex>
      </Box>
    </ChakraProvider>
  );
};

export default MainPage;
