import React, { useState } from 'react';
import { ChakraProvider, extendTheme, Box, Button, ButtonGroup, Fade } from '@chakra-ui/react';
import '@fontsource-variable/karla/wght.css';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
  components: {
    Button: {
      variants: {
        referral: {
          height: '48px',
          fontSize: '16px',
          px: '24px',
          borderRadius: 'full',
          border: '2px solid black',
          fontWeight: 700,
          textTransform: 'uppercase',
          transition: '0.3s',
          boxShadow: '0 4px 0 0 black',
          _hover: {
            transform: 'translateY(2px)',
            boxShadow: '0 2px 0 0 black',
          },
          _active: {
            transform: 'translateY(4px)',
            boxShadow: 'none',
          },
          borderRight: '0',
          _first: {
            borderTopLeftRadius: 'full',
            borderBottomLeftRadius: 'full',
          },
          _last: {
            borderRight: '2px solid black',
            borderTopRightRadius: 'full',
            borderBottomRightRadius: 'full',
          },
        },
      },
    },
  },
});

const TabButton = ({ children, isSelected, ...props }) => (
  <Button
    variant="referral"
    bg={isSelected ? '#00bfff' : 'gray.200'}
    color={isSelected ? 'black' : 'gray.600'}
    {...props}
  >
    {children}
  </Button>
);

const MainPage = () => {
  const [selectedTab, setSelectedTab] = useState('All');
  const tabs = ['All', 'Unanswered', 'Answered', 'Incorrect', 'Favorites'];

  return (
    <ChakraProvider theme={theme}>
      <Box bg="white" minHeight="100vh" display="flex" flexDirection="column" alignItems="center" px={4} py={8}>
        <ButtonGroup isAttached variant="referral" width="100%" maxWidth="800px">
          {tabs.map((tab) => (
            <TabButton
              key={tab}
              isSelected={selectedTab === tab}
              onClick={() => setSelectedTab(tab)}
              flex={1}
            >
              {tab}
            </TabButton>
          ))}
        </ButtonGroup>

        <Box mt={8} width="100%" maxWidth="800px">
          {tabs.map((tab) => (
            <Fade key={tab} in={selectedTab === tab}>
              {selectedTab === tab && <Box>Content for {tab}</Box>}
            </Fade>
          ))}
        </Box>
      </Box>
    </ChakraProvider>
  );
};

export default MainPage;
