import React from 'react';
import { Box, ChakraProvider, extendTheme, Flex } from '@chakra-ui/react';
import { MdEmail } from 'react-icons/md';
import { IoPersonAddOutline } from 'react-icons/io5';
import { BsQuestionLg } from 'react-icons/bs';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
});

const IconBox = ({
  icon: Icon,
  bgColor = '#00bfff',
  size = '48px',
  containerType = 'box',
  withBorder = true,
  borderThickness = 2, // New prop to control border thickness
  iconScale = 0.6,
  ...props
}) => {
  const iconSize = `${parseInt(size) * iconScale}px`;

  if (containerType === 'seal') {
    return (
      <Box
        position="relative"
        width={size}
        height={size}
        borderRadius="50%" // Make it a circle
        bg={bgColor}
        border={withBorder ? `${borderThickness}px solid black` : 'none'}
        {...props}
      >
        {/* Inner Icon */}
        <Flex
          position="absolute"
          top="50%"
          left="50%"
          transform="translate(-50%, -50%)"
          alignItems="center"
          justifyContent="center"
        >
          {Icon ? (
            <Icon size={iconSize} color="black" />
          ) : (
            <BsQuestionLg size={iconSize} color="black" />
          )}
        </Flex>
      </Box>
    );
  } else {
    // Original box with borderRadius
    return (
      <Flex
        width={size}
        height={size}
        bg={bgColor}
        borderRadius="12px"
        alignItems="center"
        justifyContent="center"
        {...props}
      >
        {Icon ? (
          <Icon size={iconSize} color="black" />
        ) : (
          <BsQuestionLg size={iconSize} color="black" />
        )}
      </Flex>
    );
  }
};

const IconBoxExamples = () => {
  return (
    <ChakraProvider theme={theme}>
      <Flex
        gap={4}
        p={4}
        bg="gray.100"
        justifyContent="center"
        alignItems="center"
        flexWrap="wrap"
      >
        {/* Seals with Border */}
        <IconBox
          icon={MdEmail}
          bgColor="#00bfff"
          size="64px"
          containerType="seal"
          withBorder={true}
          borderThickness={1}
          iconScale={0.5}
        />
        <IconBox
          icon={IoPersonAddOutline}
          bgColor="#b3ebf2"
          size="64px"
          containerType="seal"
          withBorder={true}
          borderThickness={3}
          iconScale={0.7}
        />
        <IconBox
          bgColor="#00bfff"
          size="64px"
          containerType="seal"
          withBorder={true}
          borderThickness={5}
          iconScale={0.6}
        />
        {/* Seals without Border */}
        <IconBox
          icon={MdEmail}
          bgColor="#00bfff"
          size="64px"
          containerType="seal"
          withBorder={false}
          iconScale={0.5}
        />
        <IconBox
          icon={IoPersonAddOutline}
          bgColor="#b3ebf2"
          size="64px"
          containerType="seal"
          withBorder={false}
          iconScale={0.7}
        />
        <IconBox
          bgColor="#00bfff"
          size="64px"
          containerType="seal"
          withBorder={false}
          iconScale={0.6}
        />
        {/* Original Box Examples */}
        <IconBox
          icon={MdEmail}
          bgColor="#00bfff"
          size="48px"
          iconScale={0.6}
        />
        <IconBox
          icon={IoPersonAddOutline}
          bgColor="#b3ebf2"
          size="48px"
          iconScale={0.6}
        />
        <IconBox bgColor="#00bfff" size="48px" iconScale={0.6} />
      </Flex>
    </ChakraProvider>
  );
};

export { IconBox, IconBoxExamples };
export default IconBoxExamples;
