import React, { useState } from 'react';
import { ChakraProvider, extendTheme, Box, Text, Flex, VStack } from '@chakra-ui/react';
import { motion, AnimatePresence } from 'framer-motion';
import { ChevronDownIcon, ChevronUpIcon } from '@chakra-ui/icons';
import '@fontsource-variable/karla/wght.css';
import { PiStar, PiStarFill, PiSealFill } from 'react-icons/pi';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
});

const MotionBox = motion(Box);

const IconBox = ({ bgColor, size, iconScale, onClick, isStarFilled }) => {
  const iconSize = `${parseInt(size) * iconScale}px`;

  return (
    <MotionBox 
      position="relative" 
      width={size} 
      height={size} 
      onClick={onClick}
      cursor="pointer"
      transition="all 0.3s ease"
      _hover={{
        '& > svg:first-of-type': {
          color: '#b3ebf2',
        }
      }}
      userSelect="none"
      whileTap={{ scale: 0.95 }}
    >
      <PiSealFill size={size} color={bgColor} />
      <Flex
        position="absolute"
        top="50%"
        left="50%"
        transform="translate(-50%, -50%)"
        alignItems="center"
        justifyContent="center"
      >
        <MotionBox 
          position="relative" 
          width={iconSize} 
          height={iconSize}
          initial={{ rotate: 0 }}
          animate={{ 
            rotate: isStarFilled ? 360 : 0,
            scale: isStarFilled ? [1, 1.2, 1] : 1
          }}
          transition={{ 
            type: "spring", 
            stiffness: 260, 
            damping: 20 
          }}
        >
          <AnimatePresence>
            {isStarFilled && (
              <MotionBox
                position="absolute"
                top={0}
                left={0}
                initial={{ opacity: 0, scale: 0.5 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.5 }}
                transition={{ duration: 0.2 }}
              >
                <PiStarFill 
                  size={iconSize} 
                  color="#FFD700" 
                />
              </MotionBox>
            )}
          </AnimatePresence>
          <PiStar 
            size={iconSize} 
            color="black" 
          />
        </MotionBox>
      </Flex>
    </MotionBox>
  );
};

const OptionBox = ({ option, isSelected, onClick }) => (
  <MotionBox
    onClick={onClick}
    bg={isSelected ? "#b3ebf2" : "white"}
    borderRadius="10px"
    border="1px solid black"
    p={4}
    cursor="pointer"
    transition="all 0.2s"
    _hover={{
      bg: isSelected ? "#b3ebf2" : "#e6f7f9",
    }}
    mb={2}
    initial={{ opacity: 0, y: -10 }}
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -10 }}
  >
    <Text fontWeight={700} fontSize="16px" lineHeight="19px" color="black">
      {option}
    </Text>
  </MotionBox>
);

const OptionsBox = ({ options }) => {
  const [isOpen, setIsOpen] = useState(true);
  const [selectedOption, setSelectedOption] = useState(null);

  const toggleDropdown = () => setIsOpen(!isOpen);

  const handleSelect = (option) => {
    setSelectedOption(option === selectedOption ? null : option);
  };

  return (
    <Box
      w="100%"
      bg="gray.200"
      borderRadius="20px"
      border="1px solid black"
      boxShadow="0 4px 0 0 black"
      position="relative"
      overflow="hidden"
      mt={4}
    >
      <Flex 
        justifyContent="space-between" 
        alignItems="center" 
        p={4}
        onClick={toggleDropdown} 
        cursor="pointer"
      >
        <Text fontSize="24px" fontWeight="700" color="black">
          Options
        </Text>
        {isOpen ? <ChevronUpIcon boxSize={6} /> : <ChevronDownIcon boxSize={6} />}
      </Flex>
      <AnimatePresence>
        {isOpen && (
          <MotionBox
            initial={{ height: 0 }}
            animate={{ height: "auto" }}
            exit={{ height: 0 }}
            transition={{ duration: 0.3 }}
            overflow="hidden"
          >
            <VStack spacing={2} align="stretch" p={4} pt={0}>
              {options.map((option, index) => (
                <OptionBox
                  key={index}
                  option={option}
                  isSelected={option === selectedOption}
                  onClick={() => handleSelect(option)}
                />
              ))}
            </VStack>
          </MotionBox>
        )}
      </AnimatePresence>
    </Box>
  );
};

const MainPage = () => {
  const [isStarFilled, setIsStarFilled] = useState(false);
  const questionNumber = 1;
  const totalQuestions = 10;
  const questionText = "A company is planning to run a global marketing application in the AWS Cloud. The application will feature videos that can be viewed by users. The company must ensure that all users can view these videos with low latency. Which AWS service should the company use to meet this requirement?";

  const toggleStar = (e) => {
    e.stopPropagation();
    setIsStarFilled(!isStarFilled);
  };

  const options = [
    "AWS Auto Scaling",
    "Amazon Kinesis Video Streams",
    "Elastic Load Balancing",
    "Amazon CloudFront"
  ];

  return (
    <ChakraProvider theme={theme}>
      <Flex justifyContent="center" alignItems="center" minHeight="100vh" bg="white" py={8}>
        <VStack w="95%" maxW="800px" spacing={4}>
          <Box
            w="100%"
            bg="gray.200"
            borderRadius="20px"
            border="1px solid black"
            boxShadow="0 4px 0 0 black"
            position="relative"
            overflow="hidden"
            p="6"
          >
            <Flex justifyContent="space-between" alignItems="center" mb="4">
              <Box
                bg="#b3ebf2"
                px="1"
                py="0"
                borderRadius="8px"
                mt="4"
              >
                <Text fontSize="24px" fontWeight="700" color="black">
                  QUESTION {questionNumber} of {totalQuestions}
                </Text>
              </Box>
              <IconBox
                bgColor="white"
                size="48px"
                iconScale={0.5}
                onClick={toggleStar}
                isStarFilled={isStarFilled}
              />
            </Flex>
            <Box mt="4">
              <Text fontSize="20px" fontWeight="500" color="black">
                {questionText}
              </Text>
            </Box>
          </Box>
          <OptionsBox options={options} />
        </VStack>
      </Flex>
    </ChakraProvider>
  );
};

export default MainPage;
