import React from 'react';
import { Box, ChakraProvider, extendTheme, Flex } from '@chakra-ui/react';
import { MdEmail } from 'react-icons/md';
import { IoPersonAddOutline } from 'react-icons/io5';
import { BsQuestionLg } from 'react-icons/bs';
import { PiSeal, PiSealFill } from 'react-icons/pi';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
});

const IconBox = ({
  icon: Icon,
  bgColor = '#00bfff',
  size = '48px',
  containerType = 'box',
  withBorder = true,
  borderThickness = '.5px', // New prop for customizable border thickness
  iconScale = 0.6,
  ...props
}) => {
  const iconSize = `${parseInt(size) * iconScale}px`;

  if (containerType === 'seal') {
    const SealIcon = PiSealFill;

    return (
      <Box position="relative" width={size} height={size} {...props}>
        <SealIcon size={size} color={bgColor} />
        {withBorder && (
          <Box
            as={PiSeal}
            size={size}
            color="black"
            position="absolute"
            top="0"
            left="0"
            style={{ strokeWidth: borderThickness }}
          />
        )}
        <Flex
          position="absolute"
          top="50%"
          left="50%"
          transform="translate(-50%, -50%)"
          alignItems="center"
          justifyContent="center"
        >
          {Icon ? (
            <Icon size={iconSize} color="black" />
          ) : (
            <BsQuestionLg size={iconSize} color="black" />
          )}
        </Flex>
      </Box>
    );
  } else {
    return (
      <Flex
        width={size}
        height={size}
        bg={bgColor}
        borderRadius="12px"
        alignItems="center"
        justifyContent="center"
        {...props}
      >
        {Icon ? (
          <Icon size={iconSize} color="black" />
        ) : (
          <BsQuestionLg size={iconSize} color="black" />
        )}
      </Flex>
    );
  }
};

const IconBoxExamples = () => {
  return (
    <ChakraProvider theme={theme}>
      <Flex
        gap={4}
        p={4}
        bg="gray.100"
        justifyContent="center"
        alignItems="center"
        flexWrap="wrap"
      >
        {/* Seals with Border - Varying sizes and border thickness */}
        <IconBox
          icon={MdEmail}
          bgColor="#00bfff"
          size="64px"
          containerType="seal"
          withBorder={true}
          borderThickness="0.5px"
          iconScale={0.4}
        />
        <IconBox
          icon={IoPersonAddOutline}
          bgColor="#b3ebf2"
          size="64px"
          containerType="seal"
          withBorder={true}
          borderThickness="1px"
          iconScale={0.5}
        />
        <IconBox
          bgColor="#00bfff"
          size="64px"
          containerType="seal"
          withBorder={true}
          borderThickness="1.5px"
          iconScale={0.6}
        />
        {/* Seals without Border - Varying sizes */}
        <IconBox
          icon={MdEmail}
          bgColor="#00bfff"
          size="56px"
          containerType="seal"
          withBorder={false}
          iconScale={0.3}
        />
        <IconBox
          icon={IoPersonAddOutline}
          bgColor="#b3ebf2"
          size="72px"
          containerType="seal"
          withBorder={false}
          iconScale={0.4}
        />
        <IconBox
          bgColor="#00bfff"
          size="80px"
          containerType="seal"
          withBorder={false}
          iconScale={0.5}
        />
        {/* Original Box Examples */}
        <IconBox
          icon={MdEmail}
          bgColor="#00bfff"
          size="48px"
          iconScale={0.6}
        />
        <IconBox
          icon={IoPersonAddOutline}
          bgColor="#b3ebf2"
          size="48px"
          iconScale={0.6}
        />
        <IconBox bgColor="#00bfff" size="48px" iconScale={0.6} />
      </Flex>
    </ChakraProvider>
  );
};

export { IconBox, IconBoxExamples };
export default IconBoxExamples;
