import React, { useState } from 'react';
import { ChakraProvider, extendTheme, Box, Button, ButtonGroup } from '@chakra-ui/react';
import '@fontsource-variable/karla/wght.css';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
  components: {
    Button: {
      variants: {
        referral: {
          height: '48px',
          fontSize: '16px',
          px: '24px',
          bg: '#abf701',
          color: 'black',
          border: '2px solid black',
          borderRight: '0',
          fontWeight: 700,
          textTransform: 'uppercase',
          transition: '0.3s',
          boxShadow: '0 4px 0 0 black',
          _hover: {
            transform: 'translateY(2px)',
            boxShadow: '0 2px 0 0 black',
          },
          _active: {
            transform: 'translateY(4px)',
            boxShadow: 'none',
          },
          _first: {
            borderTopLeftRadius: 'full',
            borderBottomLeftRadius: 'full',
          },
          _last: {
            borderRight: '2px solid black',
            borderTopRightRadius: 'full',
            borderBottomRightRadius: 'full',
          },
        },
      },
    },
  },
});

const ReferralButton = ({ children, isSelected, ...props }) => (
  <Button
    variant="referral"
    bg={isSelected ? '#8cd500' : '#abf701'}
    {...props}
  >
    {children}
  </Button>
);

const MainPage = () => {
  const [selectedTab, setSelectedTab] = useState('COPY REFERRAL LINK');

  return (
    <ChakraProvider theme={theme}>
      <Box bg="white" minHeight="100vh" display="flex" justifyContent="center" alignItems="center" px={4}>
        <ButtonGroup isAttached variant="referral" width="100%" maxWidth="800px">
          {['COPY REFERRAL LINK', 'SHARE REFERRAL', 'VIEW STATS', 'SETTINGS'].map((text) => (
            <ReferralButton
              key={text}
              flex={1}
              isSelected={selectedTab === text}
              onClick={() => setSelectedTab(text)}
            >
              {text}
            </ReferralButton>
          ))}
        </ButtonGroup>
      </Box>
    </ChakraProvider>
  );
};

export default MainPage;
