import React from 'react';
import { ChakraProvider, extendTheme, Box, Button, ButtonGroup } from '@chakra-ui/react';
import '@fontsource-variable/karla/wght.css';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
  components: {
    Button: {
      variants: {
        referral: {
          height: '48px',
          fontSize: '16px',
          px: '24px',
          bg: '#abf701',
          color: 'black',
          border: '2px solid black',
          borderRight: '0',
          fontWeight: 700,
          textTransform: 'uppercase',
          boxShadow: '0 4px 0 0 black',
          _hover: {
            bg: '#abf701',
          },
          _active: {
            bg: '#abf701',
            transform: 'translateY(4px)',
            boxShadow: 'none',
          },
          _first: {
            borderTopLeftRadius: 'full',
            borderBottomLeftRadius: 'full',
          },
          _last: {
            borderRight: '2px solid black',
            borderTopRightRadius: 'full',
            borderBottomRightRadius: 'full',
          },
        },
      },
    },
  },
});

const ReferralButton = ({ children, ...props }) => (
  <Button variant="referral" {...props}>
    {children}
  </Button>
);

const MainPage = () => {
  return (
    <ChakraProvider theme={theme}>
      <Box bg="white" minHeight="100vh" display="flex" justifyContent="center" alignItems="center" px={4}>
        <ButtonGroup isAttached variant="referral" width="100%" maxWidth="800px">
          <ReferralButton flex={1}>COPY REFERRAL LINK</ReferralButton>
          <ReferralButton flex={1}>SHARE REFERRAL</ReferralButton>
          <ReferralButton flex={1}>VIEW STATS</ReferralButton>
          <ReferralButton flex={1}>SETTINGS</ReferralButton>
        </ButtonGroup>
      </Box>
    </ChakraProvider>
  );
};

export default MainPage;
