import React from 'react';
import { Box, ChakraProvider, extendTheme, Flex } from '@chakra-ui/react';
import { MdEmail } from 'react-icons/md';
import { IoPersonAddOutline } from 'react-icons/io5';
import { BsQuestionLg } from 'react-icons/bs';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
});

const IconBox = ({ icon: Icon, bgColor = '#00bfff', size = '48px', ...props }) => {
  return (
    <Flex
      width={size}
      height={size}
      bg={bgColor}
      borderRadius="12px"
      alignItems="center"
      justifyContent="center"
      {...props}
    >
      {Icon ? (
        <Icon size={`${parseInt(size) * 0.6}px`} color="black" />
      ) : (
        <BsQuestionLg size={`${parseInt(size) * 0.6}px`} color="black" />
      )}
    </Flex>
  );
};

const IconBoxExamples = () => {
  return (
    <ChakraProvider theme={theme}>
      <Flex gap={4} p={4} bg="gray.100" justifyContent="center" alignItems="center" minHeight="100vh">
        <IconBox icon={MdEmail} bgColor="#00bfff" size="48px" />
        <IconBox icon={IoPersonAddOutline} bgColor="#b3ebf2" size="48px" />
        <IconBox bgColor="#00bfff" size="48px" />
      </Flex>
    </ChakraProvider>
  );
};

export { IconBox, IconBoxExamples };
export default IconBoxExamples;
