import React, { useState } from 'react';
import { ChakraProvider, extendTheme, Box, Text, Flex } from '@chakra-ui/react';
import '@fontsource-variable/karla/wght.css';
import { AiOutlineStar, AiFillStar } from 'react-icons/ai';
import { PiSealFill } from 'react-icons/pi';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
});

const IconBox = ({ icon: Icon, bgColor, size, iconScale, onClick, isStarFilled }) => {
  const iconSize = `${parseInt(size) * iconScale}px`;

  return (
    <Box 
      position="relative" 
      width={size} 
      height={size} 
      onClick={onClick}
      cursor="pointer"
      transition="all 0.3s ease"
      _hover={{
        '& > svg:first-of-type': {
          color: '#b3ebf2',
        }
      }}
      userSelect="none"
    >
      <PiSealFill size={size} color={bgColor} />
      <Flex
        position="absolute"
        top="50%"
        left="50%"
        transform="translate(-50%, -50%)"
        alignItems="center"
        justifyContent="center"
      >
        {isStarFilled ? (
          <Box position="relative">
            <AiFillStar size={iconSize} color="#FFD700" />
            <AiOutlineStar
              size={iconSize}
              color="black"
              style={{ position: 'absolute', top: 0, left: 0 }}
            />
          </Box>
        ) : (
          <Icon size={iconSize} color="black" />
        )}
      </Flex>
    </Box>
  );
};

const MainPage = () => {
  const [isStarFilled, setIsStarFilled] = useState(false);
  const questionNumber = 1;
  const totalQuestions = 10;
  const questionText = "A company is planning to run a global marketing application in the AWS Cloud. The application will feature videos that can be viewed by users. The company must ensure that all users can view these videos with low latency. Which AWS service should the company use to meet this requirement?";

  const toggleStar = (e) => {
    e.stopPropagation();
    setIsStarFilled(!isStarFilled);
  };

  return (
    <ChakraProvider theme={theme}>
      <Flex justifyContent="center" alignItems="center" h="100vh" bg="white">
        <Box
          w="95%"
          maxW="800px"
          bg="gray.200"
          borderRadius="20px"
          border="1px solid black"
          boxShadow="0 4px 0 0 black"
          position="relative"
          overflow="hidden"
          p="6"
        >
          <Flex justifyContent="space-between" alignItems="center" mb="4">
            <Box
              bg="#b3ebf2"
              px="1"
              py="0"
              borderRadius="8px"
              mt="4"
            >
              <Text fontSize="24px" fontWeight="700" color="black">
                QUESTION {questionNumber} of {totalQuestions}
              </Text>
            </Box>
            <IconBox
              icon={AiOutlineStar}
              bgColor="white"
              size="48px"
              iconScale={0.5}
              onClick={toggleStar}
              isStarFilled={isStarFilled}
            />
          </Flex>
          <Box mt="4">
            <Text fontSize="20px" fontWeight="500" color="black">
              {questionText}
            </Text>
          </Box>
        </Box>
      </Flex>
    </ChakraProvider>
  );
};

export default MainPage;
