import React, { useState } from 'react';
import { ChakraProvider, extendTheme, Box, Text, Flex, VStack } from '@chakra-ui/react';
import { motion, AnimatePresence } from 'framer-motion';
import { ChevronDownIcon, ChevronUpIcon } from '@chakra-ui/icons';
import '@fontsource-variable/karla/wght.css';

const theme = extendTheme({
  fonts: {
    body: '"Karla Variable", sans-serif',
  },
});

const MotionBox = motion(Box);

const OptionBox = ({ option, isSelected, onClick }) => (
  <MotionBox
    onClick={onClick}
    bg={isSelected ? "#b3ebf2" : "white"}
    borderRadius="10px"
    border="1px solid black"
    p={4}
    cursor="pointer"
    transition="all 0.2s"
    _hover={{
      bg: isSelected ? "#b3ebf2" : "#e6f7f9",
    }}
    mb={2}
    initial={{ opacity: 0, y: -10 }}
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -10 }}
  >
    <Text fontWeight={700} fontSize="16px" lineHeight="19px" color="black">
      {option}
    </Text>
  </MotionBox>
);

const OptionsBox = ({ options }) => {
  const [isOpen, setIsOpen] = useState(true);
  const [selectedOption, setSelectedOption] = useState(null);

  const toggleDropdown = () => setIsOpen(!isOpen);

  const handleSelect = (option) => {
    setSelectedOption(option === selectedOption ? null : option);
  };

  return (
    <ChakraProvider theme={theme}>
      <Box
        w="95%"
        maxW="800px"
        bg="gray.200"
        borderRadius="20px"
        border="1px solid black"
        boxShadow="0 4px 0 0 black"
        position="relative"
        overflow="hidden"
      >
        <Flex 
          justifyContent="space-between" 
          alignItems="center" 
          p={4}
          onClick={toggleDropdown} 
          cursor="pointer"
        >
          <Text fontSize="24px" fontWeight="700" color="black">
            Options
          </Text>
          {isOpen ? <ChevronUpIcon boxSize={6} /> : <ChevronDownIcon boxSize={6} />}
        </Flex>
        <AnimatePresence>
          {isOpen && (
            <MotionBox
              initial={{ height: 0 }}
              animate={{ height: "auto" }}
              exit={{ height: 0 }}
              transition={{ duration: 0.3 }}
              overflow="hidden"
            >
              <VStack spacing={2} align="stretch" p={4} pt={0}>
                {options.map((option, index) => (
                  <OptionBox
                    key={index}
                    option={option}
                    isSelected={option === selectedOption}
                    onClick={() => handleSelect(option)}
                  />
                ))}
              </VStack>
            </MotionBox>
          )}
        </AnimatePresence>
      </Box>
    </ChakraProvider>
  );
};

const MainPage = () => {
  const options = [
    "AWS Auto Scaling",
    "Amazon Kinesis Video Streams",
    "Elastic Load Balancing",
    "Amazon CloudFront"
  ];

  return (
    <Flex direction="column" alignItems="center" justifyContent="center" minHeight="100vh" bg="white" p={4}>
      {/* Insert your existing question text box component here */}
      <OptionsBox options={options} />
    </Flex>
  );
};

export default MainPage;
