import React from 'react';
import { Box, Text, Progress, Flex, useBreakpointValue, Tooltip } from '@chakra-ui/react';

const ExamCard = ({ title, progress, totalQuestions }) => {
  const cardWidth = useBreakpointValue({ base: "100%", sm: "300px", md: "350px", lg: "400px" });
  const fontSize = useBreakpointValue({ base: "16px", md: "18px", lg: "20px" });
  const buttonFontSize = useBreakpointValue({ base: "14px", md: "15px", lg: "16px" });

  return (
    <Box
      backgroundColor="white"
      borderRadius="12px"
      border="1px solid black"
      boxShadow="0 4px 0 0 black"
      padding={4}
      width={cardWidth}
      height="280px" // Increased fixed height
      flexShrink={0}
      display="flex"
      flexDirection="column"
    >
      <Tooltip label={title} aria-label="Exam title">
        <Text 
          fontSize={fontSize} 
          fontWeight="bold" 
          marginBottom={4} // Increased bottom margin
          lineHeight="1.4" // Added line height for better spacing
          height="3em" // Set a fixed height for two lines of text
          overflow="hidden"
          textOverflow="ellipsis"
          display="-webkit-box"
          style={{
            WebkitLineClamp: 2,
            WebkitBoxOrient: 'vertical'
          }}
        >
          {title}
        </Text>
      </Tooltip>
      <Box flex={1} backgroundColor="gray.200" marginBottom={4} /> {/* Increased margin */}
      <Progress value={progress} max={totalQuestions} colorScheme="blue" marginBottom={3} height="12px" />
      <Flex justifyContent="space-between" alignItems="center" marginTop={2}>
        <Text fontSize={buttonFontSize} color="gray.600">{progress} / {totalQuestions} questions</Text>
        <Box
          as="button"
          paddingX={6}
          paddingY={2}
          borderRadius="full"
          backgroundColor="#00bfff"
          color="black"
          fontWeight="bold"
          fontSize={buttonFontSize}
          border="1px solid black"
          boxShadow="0 2px 0 0 black"
          _hover={{ backgroundColor: "#00a6d6" }}
          _active={{ boxShadow: "none", transform: "translateY(2px)" }}
        >
          Continue
        </Box>
      </Flex>
    </Box>
  );
};

export default ExamCard;
