import React, { useState, useMemo } from 'react';
import { 
  Box, 
  Text, 
  HStack, 
  VStack, 
  Progress, 
  Input, 
  Flex, 
  useBreakpointValue, 
  Button 
} from '@chakra-ui/react';
import { ChevronDownIcon, ChevronUpIcon } from '@chakra-ui/icons';
import { FixedSizeList as List } from 'react-window';

const ExamCard = ({ title, progress, totalQuestions }) => {
  const cardWidth = useBreakpointValue({ base: "100%", sm: "300px", md: "350px", lg: "400px" });
  const fontSize = useBreakpointValue({ base: "16px", md: "18px", lg: "20px" });
  const imageHeight = useBreakpointValue({ base: "100px", md: "110px", lg: "120px" });
  const buttonFontSize = useBreakpointValue({ base: "14px", md: "15px", lg: "16px" });

  return (
    <Box
      backgroundColor="white"
      borderRadius="12px"
      border="1px solid black"
      boxShadow="0 4px 0 0 black"
      padding={4}
      width={cardWidth}
      flexShrink={0}
    >
      <Text fontSize={fontSize} fontWeight="bold" marginBottom={3}>{title}</Text>
      <Box height={imageHeight} backgroundColor="gray.200" marginBottom={3} />
      <Progress value={progress} max={totalQuestions} colorScheme="blue" marginBottom={2} height="12px" />
      <Flex justifyContent="space-between" alignItems="center" marginTop={2} flexDirection={{ base: "column", sm: "row" }} gap={2}>
        <Text fontSize={buttonFontSize} color="gray.600">{progress} / {totalQuestions} questions</Text>
        <Box
          as="button"
          paddingX={6}
          paddingY={2}
          borderRadius="full"
          backgroundColor="#00bfff"
          color="black"
          fontWeight="bold"
          fontSize={buttonFontSize}
          border="1px solid black"
          boxShadow="0 2px 0 0 black"
          _hover={{ backgroundColor: "#00a6d6" }}
          _active={{ boxShadow: "none", transform: "translateY(2px)" }}
        >
          Continue
        </Box>
      </Flex>
    </Box>
  );
};

const ProviderCard = ({ providerName, exams }) => {
  const fontSize = useBreakpointValue({ base: "24px", md: "26px", lg: "28px" });

  return (
    <Box
      backgroundColor="#f2f2f3"
      borderRadius="20px"
      border="1px solid black"
      boxShadow="0 4px 0 0 black"
      padding={6}
      marginBottom={8}
      width="100%"
    >
      <Text fontSize={fontSize} fontWeight="bold" marginBottom={6}>{providerName}</Text>
      <Box overflowX="auto" paddingBottom={4}>
        <HStack spacing={6}>
          {exams.map((exam, index) => (
            <ExamCard 
              key={index} 
              title={exam.title} 
              progress={exam.progress} 
              totalQuestions={exam.totalQuestions} 
            />
          ))}
        </HStack>
      </Box>
    </Box>
  );
};

const ProviderDropdown = ({ providers, selectedProvider, onSelect }) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => setIsOpen(!isOpen);

  const handleSelect = (provider) => {
    onSelect(provider);
    setIsOpen(false);
  };

  const sortedProviders = useMemo(() => {
    return ["All Providers", ...providers.sort((a, b) => a.localeCompare(b))];
  }, [providers]);

  return (
    <Box position="relative" width="300px">
      <Button
        onClick={toggleDropdown}
        width="100%"
        backgroundColor="white"
        color="black"
        fontWeight={700}
        fontSize="16px"
        lineHeight="19px"
        borderRadius="10px"
        border="1px solid black"
        boxShadow="none"
        _hover={{
          backgroundColor: "#00bfff",
          boxShadow: "0 3px 0 0 black",
        }}
        _active={{
          backgroundColor: "#00bfff",
          boxShadow: "none",
        }}
        transition="all 0.2s"
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        paddingX={4}
        paddingY={3}
      >
        <Text>{selectedProvider}</Text>
        {isOpen ? <ChevronUpIcon /> : <ChevronDownIcon />}
      </Button>
      {isOpen && (
        <Box
          position="absolute"
          top="100%"
          left={0}
          width="100%"
          height="300px"
          backgroundColor="white"
          borderRadius="10px"
          border="1px solid black"
          marginTop={2}
          zIndex={1}
          overflow="hidden"
        >
          <List
            height={300}
            itemCount={sortedProviders.length}
            itemSize={40}
            width="100%"
          >
            {({ index, style }) => (
              <Box
                style={style}
                paddingY={2}
                paddingX={4}
                cursor="pointer"
                backgroundColor={sortedProviders[index] === selectedProvider ? "#b3ebf2" : "white"}
                _hover={{ backgroundColor: "#e6f7f9" }}
                onClick={() => handleSelect(sortedProviders[index])}
                borderBottom={index < sortedProviders.length - 1 ? "1px solid #E2E8F0" : "none"}
              >
                <Text 
                  fontWeight={700} 
                  fontSize="16px" 
                  lineHeight="19px" 
                  color={sortedProviders[index] === selectedProvider ? "black" : "gray.700"}
                >
                  {sortedProviders[index]}
                </Text>
              </Box>
            )}
          </List>
        </Box>
      )}
    </Box>
  );
};

