import React from 'react';
import { Box, Text, VStack, HStack, Progress, Image, Button } from '@chakra-ui/react';

const ExamItem = ({ title, progress, totalQuestions, badgeUrl }) => (
  <HStack spacing={4} alignItems="center" width="100%" position="relative" _hover={{ bg: "gray.100" }}>
    <Box 
      position="absolute" 
      left="-20px" 
      top="0" 
      bottom="0" 
      width="1px" 
      bg="gray.300"
      _before={{
        content: '""',
        position: "absolute",
        left: "-10px",
        top: "50%",
        width: "10px",
        height: "1px",
        bg: "gray.300",
      }}
    />
    <Image src={badgeUrl} alt={title} boxSize="30px" />
    <Text flex={1}>{title}</Text>
    <Progress value={progress} max={totalQuestions} width="100px" />
    <Text width="80px" textAlign="right">{progress} / {totalQuestions}</Text>
    <Button 
      size="sm" 
      colorScheme="blue"
      onClick={() => console.log(`Continue ${title}`)}
    >
      Continue
    </Button>
  </HStack>
);

const ProviderList = ({ providers }) => {
  return (
    <VStack align="stretch" spacing={6}>
      {providers.map((provider, index) => (
        <Box key={index} borderWidth={1} borderRadius="lg" p={4} position="relative">
          <Text fontSize="xl" fontWeight="bold" mb={4}>{provider.name}</Text>
          <VStack align="stretch" spacing={2} pl={6}>
            {provider.exams.map((exam, examIndex) => (
              <ExamItem
                key={examIndex}
                title={exam.title}
                progress={exam.progress}
                totalQuestions={exam.totalQuestions}
                badgeUrl={exam.badgeUrl || "/api/placeholder/30/30"}
              />
            ))}
          </VStack>
        </Box>
      ))}
    </VStack>
  );
};

export default ProviderList;
