import React, { useRef, useEffect, useState } from 'react';
import { Flex, Text, Link, Tooltip } from '@chakra-ui/react';
import { ChevronRightIcon } from '@chakra-ui/icons';

const Breadcrumbs = ({ items }) => {
  const containerRef = useRef(null);
  const itemRefs = useRef([]);
  const [truncatedItems, setTruncatedItems] = useState([]);

  useEffect(() => {
    const checkOverflow = () => {
      if (containerRef.current) {
        const container = containerRef.current;
        let availableWidth = container.offsetWidth;
        const newTruncatedItems = [];

        // Always show first and last items
        const firstItem = itemRefs.current[0];
        const lastItem = itemRefs.current[itemRefs.current.length - 1];
        if (firstItem && lastItem) {
          availableWidth -= firstItem.offsetWidth + lastItem.offsetWidth + 32; // 32px for chevrons
        }

        // Check middle items for truncation
        for (let i = 1; i < items.length - 1; i++) {
          const itemRef = itemRefs.current[i];
          if (itemRef) {
            if (availableWidth - itemRef.offsetWidth - 16 < 0) { // 16px for chevron
              newTruncatedItems[i] = true;
              availableWidth -= 50; // Approximate width for truncated item
            } else {
              availableWidth -= itemRef.offsetWidth + 16;
            }
          }
        }

        setTruncatedItems(newTruncatedItems);
      }
    };

    checkOverflow();
    window.addEventListener('resize', checkOverflow);
    return () => window.removeEventListener('resize', checkOverflow);
  }, [items]);

  const renderItem = (item, index) => {
    const isFirst = index === 0;
    const isLast = index === items.length - 1;
    const shouldTruncate = !isFirst && !isLast && truncatedItems[index];

    const content = (
      <Text
        color={item.isCurrentPage ? "gray.500" : "blue.500"}
        fontWeight="500"
        fontSize="16px"
        whiteSpace="nowrap"
        overflow="hidden"
        textOverflow="ellipsis"
        maxWidth={shouldTruncate ? "50px" : "none"}
        ref={el => itemRefs.current[index] = el}
        as={item.isCurrentPage ? "span" : Link}
        href={item.isCurrentPage ? undefined : item.href}
        _hover={item.isCurrentPage ? undefined : { textDecoration: 'underline' }}
      >
        {shouldTruncate ? item.label.slice(0, 3) + '...' : item.label}
      </Text>
    );

    return shouldTruncate ? (
      <Tooltip key={index} label={item.label} aria-label="Full breadcrumb text">
        {content}
      </Tooltip>
    ) : content;
  };

  return (
    <Flex align="center" ref={containerRef} overflow="hidden" width="100%">
      {items.map((item, index) => (
        <React.Fragment key={index}>
          {index > 0 && <ChevronRightIcon mx={1} color="gray.500" flexShrink={0} boxSize={3} />}
          {renderItem(item, index)}
        </React.Fragment>
      ))}
    </Flex>
  );
};

export default Breadcrumbs;
