import React, { useState, useMemo, useRef, useEffect } from 'react';
import { 
  Box, 
  Text, 
  HStack, 
  VStack, 
  Progress, 
  Input, 
  Flex, 
  useBreakpointValue, 
  Button,
  IconButton
} from '@chakra-ui/react';
import { ChevronDownIcon, ChevronUpIcon } from '@chakra-ui/icons';
import { FixedSizeList as List } from 'react-window';
import { LuGrid, LuList } from "react-icons/lu";

const ExamCard = ({ title, progress, totalQuestions }) => {
  const cardWidth = useBreakpointValue({ base: "100%", sm: "300px", md: "350px", lg: "400px" });
  const fontSize = useBreakpointValue({ base: "16px", md: "18px", lg: "20px" });
  const imageHeight = useBreakpointValue({ base: "100px", md: "110px", lg: "120px" });
  const buttonFontSize = useBreakpointValue({ base: "14px", md: "15px", lg: "16px" });

  return (
    <Box
      backgroundColor="white"
      borderRadius="12px"
      border="1px solid black"
      boxShadow="0 4px 0 0 black"
      padding={4}
      width={cardWidth}
      flexShrink={0}
    >
      <Text fontSize={fontSize} fontWeight="bold" marginBottom={3}>{title}</Text>
      <Box height={imageHeight} backgroundColor="gray.200" marginBottom={3} />
      <Progress value={progress} max={totalQuestions} colorScheme="blue" marginBottom={2} height="12px" />
      <Flex justifyContent="space-between" alignItems="center" marginTop={2} flexDirection={{ base: "column", sm: "row" }} gap={2}>
        <Text fontSize={buttonFontSize} color="gray.600">{progress} / {totalQuestions} questions</Text>
        <Box
          as="button"
          paddingX={6}
          paddingY={2}
          borderRadius="full"
          backgroundColor="#00bfff"
          color="black"
          fontWeight="bold"
          fontSize={buttonFontSize}
          border="1px solid black"
          boxShadow="0 2px 0 0 black"
          _hover={{ backgroundColor: "#00a6d6" }}
          _active={{ boxShadow: "none", transform: "translateY(2px)" }}
        >
          Continue
        </Box>
      </Flex>
    </Box>
  );
};

const ProviderCard = ({ providerName, exams }) => {
  const fontSize = useBreakpointValue({ base: "24px", md: "26px", lg: "28px" });

  return (
    <Box
      backgroundColor="#f2f2f3"
      borderRadius="20px"
      border="1px solid black"
      boxShadow="0 4px 0 0 black"
      padding={6}
      marginBottom={8}
      width="100%"
    >
      <Text fontSize={fontSize} fontWeight="bold" marginBottom={6}>{providerName}</Text>
      <Box overflowX="auto" paddingBottom={4}>
        <HStack spacing={6}>
          {exams.map((exam, index) => (
            <ExamCard 
              key={index} 
              title={exam.title} 
              progress={exam.progress} 
              totalQuestions={exam.totalQuestions} 
            />
          ))}
        </HStack>
      </Box>
    </Box>
  );
};

const ProviderDropdown = ({ providers, selectedProvider, onSelect }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [dropdownWidth, setDropdownWidth] = useState(300); // Default width
  const textRef = useRef(null);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleSelect = (provider) => {
    onSelect(provider);
    setIsOpen(false);
  };

  const sortedProviders = useMemo(() => {
    return ["All Providers", ...providers.sort((a, b) => a.localeCompare(b))];
  }, [providers]);

  useEffect(() => {
    if (textRef.current) {
      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d');
      context.font = '700 16px "Karla Variable", sans-serif'; // Match the Text component's font

      const maxWidth = Math.max(...sortedProviders.map(provider => 
        context.measureText(provider).width
      ));

      // Add some padding (e.g., 40px) to account for button padding and dropdown arrow
      setDropdownWidth(Math.ceil(maxWidth) + 40);
    }
  }, [sortedProviders]);

  return (
    <Box position="relative" width={`${dropdownWidth}px`}>
      <Button
        onClick={toggleDropdown}
        width="100%"
        backgroundColor="white"
        color="black"
        fontWeight={700}
        fontSize="16px"
        lineHeight="19px"
        borderRadius="10px"
        border="1px solid black"
        boxShadow="none"
        _hover={{
          backgroundColor: "#00bfff",
          boxShadow: "0 3px 0 0 black",
        }}
        _active={{
          backgroundColor: "#00bfff",
          boxShadow: "none",
        }}
        transition="all 0.2s"
        display="flex"
        justifyContent="space-between"
        alignItems="center"
        paddingLeft={4}
        paddingRight={4}
        paddingTop={3}
        paddingBottom={3}
      >
        <Text ref={textRef}>{selectedProvider}</Text>
        {isOpen ? <ChevronUpIcon /> : <ChevronDownIcon />}
      </Button>
      {isOpen && (
        <Box
          position="absolute"
          top="100%"
          left={0}
          width="100%"
          height="300px"
          backgroundColor="white"
          borderRadius="10px"
          border="1px solid black"
          marginTop={2}
          zIndex={1}
          overflow="hidden"
        >
          <List
            height={300}
            itemCount={sortedProviders.length}
            itemSize={40}
            width="100%"
          >
            {({ index, style }) => (
              <Box
                style={style}
                paddingTop={2}
                paddingBottom={2}
                paddingLeft={4}
                paddingRight={4}
                cursor="pointer"
                backgroundColor={sortedProviders[index] === selectedProvider ? "#00bfff" : "white"}
                _hover={{ 
                  backgroundColor: sortedProviders[index] === selectedProvider ? "#00bfff" : "#b3ebf2" 
                }}
                onClick={() => handleSelect(sortedProviders[index])}
                borderBottom={index < sortedProviders.length - 1 ? "1px solid #E2E8F0" : "none"}
              >
                <Text 
                  fontWeight={700} 
                  fontSize="16px" 
                  lineHeight="19px" 
                  color="black"
                >
                  {sortedProviders[index]}
                </Text>
              </Box>
            )}
          </List>
        </Box>
      )}
    </Box>
  );
};

const ProviderExamsCard = () => {
